import "./MoviesCardList.scss";
import React from "react";
import MoviesCard from "../MoviesCard/MoviesCard";

function MoviesCardList({ slice, onCardClick, onCardLike }) {
  return (
    <section className='movies-list'>
      {slice.map((movie) => {
        return (
          <div className='movie-card'>
            <img className='movie-card__image' src='' alt='film'></img>
            <div className='movie-card__button-box'>
              <button
                aria-label='Лайк'
                className='movie-card__save-button'
                type='button'
              >
                Сохранить
              </button>
            </div>
            <div className='movie-card__text'>
              <h3 className='movie-card__title'>{movie.nameRU}</h3>
              <p className='movie-card__duration'>
                {Math.floor(movie.duration / 60)}ч {movie.duration % 60}м
              </p>
            </div>
          </div>
          // <MoviesCard
          //   movie={movie}
          //   key={movie.id}
          //   onCardClick={onCardClick}
          //   onCardLike={onCardLike}
          // />
        );
      })}
    </section>
  );
}

export default MoviesCardList;
