import "./app.scss";
import Main from "../Main/Main";
import Register from "../Register/Register";
import Login from "../Login/Login";
import Movies from "../Movies/Movies";
import SavedMovies from "../SavedMovies/SavedMovies";
import Profile from "../Profile/Profile";
import NotFoundPage from "../NotFoundPage/NotFoundPage";
import ModalMenu from "../ModalMenu/ModalMenu";

import { Routes, Route } from "react-router-dom";
import React, { useState, useEffect } from "react";
import { moviesApi } from "../../utils/MoviesApi";
import { mainApi } from "../../utils/MainApi";

function App() {
  const [checked, setChecked] = useState(false);
  console.log(checked);
  const [isSideMenuOpen, setSideMenuOpen] = useState(false);
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  function handleSaveMovie(
    country,
    director,
    duration,
    year,
    description,
    image,
    trailerLink,
    nameRU,
    nameEN,
    thumbnail,
    movieId
  ) {
    const isLiked = card.likes.filter((i) => i._id === currentUser._id);

    mainApi
      .saveMovie(
        country,
        director,
        duration,
        year,
        description,
        image,
        trailerLink,
        nameRU,
        nameEN,
        thumbnail,
        movieId
      )
      .then((movie) => {
        console.log(movie);
      })
      .catch((error) => {
        console.log(error);
      });

    if (!isLiked) {
      api
        .setLike(card._id)
        .then((newCard) => {
          setCards((state) =>
            state.map((c) => (c._id === card._id ? newCard : c))
          );
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      api
        .deleteLike(card._id)
        .then((newCard) => {
          setCards((state) =>
            state.map((c) => (c._id === card._id ? newCard : c))
          );
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }

  const handleSearchClick = () => {
    setIsLoading(true);
    // tokenCheck()
    // if (loggedIn) {
    moviesApi
      .getMovies()
      .then((movies) => {
        console.log(movies);
        localStorage.setItem("movies", JSON.stringify(movies));
        const localMovies = JSON.parse(localStorage.getItem("movies"));
        console.log(localMovies);
        const searchReq = JSON.parse(
          localStorage.getItem("searchReq").toLowerCase()
        );
        console.log(searchReq.search);
        const showMovies = localMovies.filter((i) =>
          i.nameRU.toLowerCase().match(searchReq.search)
        );
        console.log(showMovies);

        setMovies([...showMovies]);

        // setCurrentUser(userData);
      })
      .catch((error) => {
        console.log(error);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  function handleSideMemuClick() {
    setSideMenuOpen(!isSideMenuOpen);
  }
  function closeMenu() {
    setSideMenuOpen(false);
  }
  useEffect(() => {
    localStorage.setItem("isShortFilm", JSON.stringify(checked));
  }, [checked]);

  // useEffect(() => {
  //   setIsLoading(true);
  //   // tokenCheck()
  //   // if (loggedIn) {
  //   api
  //     .getMovies()
  //     .then((movies) => {
  //       setMovies([...movies]);
  //       // setCurrentUser(userData);
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     })
  //     .finally(() => {
  //       setIsLoading(false);
  //     });
  //   // }
  // }, []);

  return (
    <div className='page'>
      <Routes>
        <Route path='/' element={<Main />} />
        <Route path='signup' element={<Register />} />
        <Route path='signin' element={<Login />} />
        <Route
          path='movies'
          element={
            <Movies
              isLoading={isLoading}
              movies={movies}
              onMenuClick={handleSideMemuClick}
              onSearchClick={handleSearchClick}
              checked={checked}
              onChange={setChecked}
            />
          }
        />
        <Route path='saved-movies' element={<SavedMovies movies={movies} />} />
        <Route
          path='profile'
          element={<Profile onMenuClick={handleSideMemuClick} />}
        />
        <Route path='404' element={<NotFoundPage />} />
      </Routes>
      <ModalMenu isOpen={isSideMenuOpen} onClose={closeMenu} />
    </div>
  );
}

export default App;
