import "./navigation.scss";
import { Link, NavLink } from "react-router-dom";
function Navigation({onMenuClick}) {
  return (
    <nav className='navigation'>
      <div className='navigation__logo'></div>
      <div className='navigation__menu'>

      <NavLink to="/movies" className={({isActive}) => `${isActive ? "navigation__link_active" : "navigation__link"}`}>Фильмы</NavLink>
        {/* <Link className='navigation__link' to='/movies'>
          Фильмы
        </Link> */}
<NavLink to="/movies" className={({isActive}) => `${isActive ? "navigation__link_active" : "navigation__link"}`}>Сохраненные фильмы</NavLink>
        {/* <Link className='navigation__link' to='/saved-movies'>
          Сохраненные фильмы
        </Link> */}
      </div>
      <div className='navigation__profile'>
        <Link className='navigation__button' to='/profile'>
          <p className='navigation__text'>Аккаунт</p>
        </Link>
        <div className='navigation__profile-logo' onClick={onMenuClick}>
          <div className='navigation__profile-logo-image' onClick={onMenuClick}></div>
        </div>
      </div>
    </nav>
  );
}

export default Navigation;
