import "./movies.scss";
import React from "react";
import Header from "../Header/Header";
import SearchForm from "./SearchForm/SerachForm";
import MoviesCardList from "./MoviesCardList/MoviesCardList";
import Footer from "../Footer/Footer";
import Navigation from "../Navigation/Navigation";
import ModalMenu from "../ModalMenu/ModalMenu";
import { useState, useEffect } from "react";

function Movies({ movies }) {
  const [isSideMenuOpen, setSideMenuOpen] = useState(false);
  console.log(isSideMenuOpen)
  const isOpen = isSideMenuOpen;

  const [noOfElement, setNoOfElement] = useState(12);
  const [numShowMovies, setNumShowMovies] = useState(3);
  const slice = movies.slice(0, noOfElement);
  console.log(noOfElement);
  const loadmore = () => {
    setNoOfElement(noOfElement + numShowMovies);
  };
  useEffect(() => {
    function handleResize() {
      console.log("resized to: ", window.innerWidth, "x", window.innerHeight);
      if (window.innerWidth < 1170) {
        setNoOfElement(8);
        setNumShowMovies(2);
      }
      if (window.innerWidth < 550) {
        setNoOfElement(5);
        setNumShowMovies(1);
      } else if (window.innerWidth > 1170) {
        setNoOfElement(12);
        setNumShowMovies(3);
      }
    }

    window.addEventListener("resize", handleResize);
    return (_) => {
      window.removeEventListener("resize", handleResize);
    };
  });

  function handleSideMemuClick() {
    setSideMenuOpen(!isSideMenuOpen);
  }
  function closeMenu(){
    setSideMenuOpen(false)
  }

  return (
    <div className='movies'>
      {/* <Header/> */}
      <Navigation onMenuClick={handleSideMemuClick} />
      <SearchForm />
      <ModalMenu isOpen={isSideMenuOpen} />
      <MoviesCardList slice={slice} />
      <div className='movies__more'>
        <button className='movies__button' onClick={loadmore}>
          Ещё
        </button>
      </div>

      <Footer />
    </div>
  );
}

export default Movies;
