import "./savedMovies.scss";
import React, { useState } from "react";
import Navigation from "../Navigation/Navigation";
import SearchForm from "../SearchForm/SerachForm";
import MoviesCardList from "../MoviesCardList/MoviesCardList";
import Footer from "../Footer/Footer";
import film1 from "../../images/film1.png";

function SavedMovies({ movies }) {
  const [isDeleteButton, setShowDeleteButton] = useState(false);
  const showLikeButtonClassName = `movie-card__delete-button ${
    isDeleteButton && "movie-card__save-button_show"
  }`;

  function showDeleteButton() {
    setShowDeleteButton(true);
  }
  function hideDeleteButton() {
    setShowDeleteButton(false);
  }

  return (
    <div className='saved-movies'>
      <Navigation />
      <SearchForm />
      <section className='movies-list'>
        <div className='movie-card'>
          <img
            className='movie-card__image'
            src={film1}
            alt='film'
            onMouseOver={showDeleteButton}
            onMouseLeave={hideDeleteButton}
          ></img>

          <button
            aria-label='Лайк'
            className='movie-card__delete-button'
            type='button'
          ></button>

          <div className='movie-card__text'>
            <h3 className='movie-card__title'>Film</h3>
            <p className='movie-card__duration'>1ч 22м</p>
          </div>
        </div>
        <div className='movie-card'>
          <img className='movie-card__image' src={film1} alt='film'></img>
          <div className='movie-card__button-box'>
            <button
              aria-label='Лайк'
              className='movie-card__save-button'
              type='button'
            >
              Сохранить
            </button>
          </div>
          <div className='movie-card__text'>
            <h3 className='movie-card__title'>Film</h3>
            <p className='movie-card__duration'>1ч 22м</p>
          </div>
        </div>
        <div className='movie-card'>
          <img className='movie-card__image' src={film1} alt='film'></img>
          <div className='movie-card__button-box'>
            <button
              aria-label='Лайк'
              className='movie-card__save-button'
              type='button'
            >
              Сохранить
            </button>
          </div>
          <div className='movie-card__text'>
            <h3 className='movie-card__title'>Film</h3>
            <p className='movie-card__duration'>1ч 22м</p>
          </div>
        </div>
      </section>
      <Footer />
    </div>
  );
}

export default SavedMovies;
