import "./moviesCard.scss";
import React from "react";

function MoviesCard({movie, handleLikeClick, isLiked}) {
  const cardLikeButtonClassName = `movieCard__save-button ${isLiked && "card__icon_active"}`;

    const url = 'https://api.nomoreparties.co'
  return (

      <div className='movieCard'>
        <img
          className='movieCard__image'
          src={`${url}${movie.image.url}`}
          alt='film'
        ></img>
         <div className="movieCard__button-box">
          <button
            aria-label="Лайк"
            className={cardLikeButtonClassName}
            type="button"
            onClick={handleLikeClick}
          >Сохранить</button>

        </div>
        <div className='movieCard__text'>
          <h3 className='movieCard__title'>{movie.nameRU
}</h3>
          <p className='movieCard__duration'>{Math.floor(movie.duration / 60)}ч {movie.duration % 60}м</p>
        </div>
      </div>


  );
}

export default MoviesCard;
