import "./movies.scss";
import React from "react";
import Header from "../Header/Header";
import SearchForm from "./SearchForm/SerachForm";
import MoviesCardList from "./MoviesCardList/MoviesCardList";
import Footer from "../Footer/Footer";
import Navigation from "../Navigation/Navigation";

function Movies({movies, loadMore}) {
    const [noOfElement, setNoOfElement] = useState(12);
    const slice = movies.slice(0, noOfElement);
    const loadmore = () => {
      setNoOfElement(noOfElement + 3);
    };
  return (
    <div className='movies'>
      {/* <Header/> */}
      <Navigation />
      <SearchForm />
      <MoviesCardList movies={movies}/>
      <div className='moviesCardList__more'>
        <button className='moviesCardList__button' onClick={loadMore}>Ещё</button>
      </div>
      <Footer />
    </div>
  );
}

export default Movies;
