import "./movies.scss";
import React from "react";
import Header from "../Header/Header";
import SearchForm from "./SearchForm/SerachForm";
import MoviesCardList from "./MoviesCardList/MoviesCardList";
import Footer from "../Footer/Footer";
import Navigation from "../Navigation/Navigation";
import { useState, useEffect } from "react";

function Movies({ movies }) {
    useEffect(() => {
        function handleResize() {
          console.log("resized to: ", window.innerWidth, "x", window.innerHeight);
          if (window.innerWidth < 1170) {
            setNoOfElement(8)
          } else if (window.innerWidth < 550) {
            setNoOfElement(5)
          } else if (window.innerWidth > 1170) {
            setNoOfElement(12)
          }

        }

        window.addEventListener("resize", handleResize);
      });

  const [noOfElement, setNoOfElement] = useState(12);
  const slice = movies.slice(0, noOfElement);
  let numShowMovies = 3;
  console.log(noOfElement)
  const loadmore = () => {
    setNoOfElement(noOfElement + 3);
  };


  return (
    <div className='movies'>
      {/* <Header/> */}
      <Navigation />
      <SearchForm />
      <MoviesCardList slice={slice} />
      <div className='movies__more'>
        <button className='movies__button' onClick={loadmore}>
          Ещё
        </button>
      </div>
      <Footer />
    </div>
  );
}

export default Movies;
